import React from 'react';
import './home.css';



function Home() {  
    // State cho các bộ lọc
  const [industry, setIndustry] = useState('');
  const [salary, setSalary] = useState('');
  const [location, setLocation] = useState('');

  // Dữ liệu cứng (hardcode) cho ngành nghề, mức lương, địa điểm
  const industries = [
    'Kinh doanh', 'Phiên dịch', 'Báo chí', 'Bưu chính - Viễn thông', 'Game', 'IT'
  ];
  const salaries = ['Tất cả', 'Dưới 10 triệu', '10-20 triệu', '20-30 triệu', 'Trên 30 triệu'];
  const locations = ['Tất cả', 'Hà Nội', 'Thành phố Hồ Chí Minh', 'Miền Bắc', 'Miền Nam'];

  return (
    <div className="job-portal-container">
      {/* Hàng đầu tiên - Việc làm tốt nhất và nút Xem tất cả */}
      <div className="header">
        <h1>Việc làm tốt nhất</h1>
        <button className="view-all-btn" onClick={() => alert('Chuyển sang trang khác')}>Xem tất cả</button>
      </div>

      {/* Hàng thứ hai - Bộ lọc */}
      <div className="filter-section">
        <div className="filter-box">
          <h3>Bộ lọc</h3>
          <div className="filter-category">
            <label>Ngành nghề:</label>
            <select value={industry} onChange={(e) => setIndustry(e.target.value)}>
              <option value="">Tất cả</option>
              {industries.map((item, index) => (
                <option key={index} value={item}>{item}</option>
              ))}
            </select>
          </div>

          <div className="filter-category">
            <label>Địa điểm:</label>
            <select value={location} onChange={(e) => setLocation(e.target.value)}>
              {locations.map((item, index) => (
                <option key={index} value={item}>{item}</option>
              ))}
            </select>
          </div>

          <div className="filter-category">
            <label>Mức lương:</label>
            <select value={salary} onChange={(e) => setSalary(e.target.value)}>
              {salaries.map((item, index) => (
                <option key={index} value={item}>{item}</option>
              ))}
            </select>
          </div>
        </div>

        {/* Phần bên phải (Có thể thêm gì khác tùy vào yêu cầu) */}
        <div className="filter-right">
          <p>Bạn có thể tùy chỉnh bộ lọc theo nhu cầu của bạn.</p>
        </div>
      </div>

      {/* Hàng thứ ba - Danh sách các job (flexbox) */}
      <div className="job-list-section">
        <JobList />
      </div>
    </div>
  );
}

export default Home;
