import React, { useState } from 'react';
import './home.css';
import JobList from './JobList.js';



function Home() {  
    const filterOptions = [
        { name: 'Ngành nghề', values: ['Kinh doanh', 'Phiên dịch', 'Báo chí', 'Bưu chính - Viễn thông', 'Game', 'IT'] },
        { name: 'Mức lương', values: ['Tất cả', 'Dưới 10 triệu', '10-20 triệu', '20-30 triệu', 'Trên 30 triệu'] },
        { name: 'Địa điểm', values: ['Tất cả', 'Hà Nội', 'Thành phố Hồ Chí Minh', 'Miền Bắc', 'Miền Nam'] }
      ];
    
      const [selectedFilter, setSelectedFilter] = useState(filterOptions[0]); // Mặc định chọn "Ngành nghề"
      const [selectedValue, setSelectedValue] = useState(''); // Giá trị cho bộ lọc
    
      return (
        <div className="job-portal-container">
          {/* Hàng đầu tiên - Việc làm tốt nhất và nút Xem tất cả */}
          <div className="header">
            <h1>Việc làm tốt nhất</h1>
            <button className="view-all-btn" onClick={() => alert('Chuyển sang trang khác')}>Xem tất cả</button>
          </div>
    
             {/* Bộ lọc */}
      <div className="filter-container">
        <div className="filter-left">
          <label>Lọc theo:</label>
          <select onChange={handleFilterChange}>
            <option value="nganh-nghe">Ngành nghề</option>
            <option value="muc-luong">Mức lương</option>
            <option value="dia-diem">Địa điểm</option>
          </select>
        </div>

        <div className="filter-right">
          <button className="arrow-btn" onClick={handlePreviousClick}>{"<"}</button>
          <div className="filter-values">
            {currentValues.map((value, index) => (
              <span key={index} className="filter-value">{value}</span>
            ))}
          </div>
          <button className="arrow-btn" onClick={handleNextClick}>{">"}</button>
        </div>
      </div>
    
          {/* Hàng thứ ba - Danh sách các job */}
          <div className="job-list-section">
            <JobList />
          </div>
        </div>
      );
}

export default Home;
